entry.value:
drop database if exists twitter;
create database twitter;
use twitter;

create table tweets
(
  country string DEFAULT 'Unknown',
  screen_name string,
  created_at string,
  lang string,
  id  string,
  CONSTRAINT primary PRIMARY KEY (country, id ASC)
);

--create user jack with password dorsey;
--grant all on database twitter to jack;
--grant all on table tweets to jack;


ALTER TABLE tweets PARTITION BY LIST (country) (
  PARTITION UnitedStates VALUES IN ('United States'),
  PARTITION Canada VALUES IN ('Canada'),
  PARTITION Mexico VALUES IN ('Mexico'),
  PARTITION Unknown VALUES IN ('Unknown'),
  PARTITION NonNA VALUES IN (default)
);
